  private QMUITabBuilder mTabBuilder;
  private Animator mSelectAnimator;
  private OnTabClickListener mOnTabClickListener;
  private ViewPager mViewPager;
  private PagerAdapter mPagerAdapter;
  private DataSetObserver mPagerAdapterObserver;
  private ViewPager.OnPageChangeListener mOnPageChangeListener;
  private OnTabSelectedListener mViewPagerSelectedListener;
  private AdapterChangeListener mAdapterChangeListener;
  private boolean mIsInSelectTab=false;
  private QMUILayoutHelper mLayoutHelper;
  private Map<String,Integer> mDefaultSkinAttrs=new HashMap<>();
  public QMUITabSegment(  Context context){
    this(context,null);
  }
  public QMUITabSegment(  Context context,  AttributeSet attrs){
    this(context,attrs,R.attr.QMUITabSegmentStyle);
  }
  public QMUITabSegment(  Context context,  AttributeSet attrs,  int defStyleAttr){
    super(context,attrs,defStyleAttr);
    setWillNotDraw(false);
    mLayoutHelper=new QMUILayoutHelper(context,attrs,defStyleAttr,this);
    init(context,attrs,defStyleAttr);
    setHorizontalScrollBarEnabled(false);
    setClipToPadding(false);
    setClipChildren(false);
  }
  private void init(  Context context,  AttributeSet attrs,  int defStyleAttr){
    TypedArray array=context.obtainStyledAttributes(attrs,R.styleable.QMUITabSegment,defStyleAttr,0);
    boolean hasIndicator=array.getBoolean(R.styleable.QMUITabSegment_qmui_tab_has_indicator,false);
    int indicatorHeight=array.getDimensionPixelSize(R.styleable.QMUITabSegment_qmui_tab_indicator_height,getResources().getDimensionPixelSize(R.dimen.qmui_tab_segment_indicator_height));
    boolean indicatorTop=array.getBoolean(R.styleable.QMUITabSegment_qmui_tab_indicator_top,false);
    boolean indicatorWidthFollowContent=array.getBoolean(R.styleable.QMUITabSegment_qmui_tab_indicator_with_follow_content,false);
    mIndicator=createTabIndicatorFromXmlInfo(hasIndicator,indicatorHeight,indicatorTop,indicatorWidthFollowContent);
    int normalTextSize=array.getDimensionPixelSize(R.styleable.QMUITabSegment_android_textSize,getResources().getDimensionPixelSize(R.dimen.qmui_tab_segment_text_size));
    normalTextSize=array.getDimensionPixelSize(R.styleable.QMUITabSegment_qmui_tab_normal_text_size,normalTextSize);
    int selectedTextSize=normalTextSize;
    selectedTextSize=array.getDimensionPixelSize(R.styleable.QMUITabSegment_qmui_tab_selected_text_size,selectedTextSize);
    mTabBuilder=new QMUITabBuilder(context).setTextSize(normalTextSize,selectedTextSize).setIconPosition(array.getInt(R.styleable.QMUITabSegment_qmui_tab_icon_position,QMUITab.ICON_POSITION_LEFT));
    mMode=array.getInt(R.styleable.QMUITabSegment_qmui_tab_mode,MODE_FIXED);
    mItemSpaceInScrollMode=array.getDimensionPixelSize(R.styleable.QMUITabSegment_qmui_tab_space,QMUIDisplayHelper.dp2px(context,10));
    array.recycle();
    mContentLayout=new Container(context);
    addView(mContentLayout,new FrameLayout.LayoutParams(ViewGroup.LayoutParams.WRAP_CONTENT,ViewGroup.LayoutParams.MATCH_PARENT));
    mTabAdapter=createTabAdapter(mContentLayout);
    mDefaultSkinAttrs.put(QMUISkinValueBuilder.BOTTOM_SEPARATOR,R.attr.qmui_tab_separator_color);
    mDefaultSkinAttrs.put(QMUISkinValueBuilder.TOP_SEPARATOR,R.attr.qmui_tab_separator_color);
    mDefaultSkinAttrs.put(QMUISkinValueBuilder.BACKGROUND,R.attr.qmui_tab_bg);
  }
