/** 
 * <p> Tab  Tab</p> <ul> <li> xml  QMUITabSegment  set icon </li> <li> Tab  QMUITabSegment   {@link QMUITab}</li> <li>  {@link #setupWithViewPager(ViewPager)}  {@link ViewPager} </li></ul> <p> <h3>case: </h3> <ul> <li>   {@link ViewPager}  {@link PagerAdapter}  {@link PagerAdapter#getPageTitle(int)} ,  {@link #setupWithViewPager(ViewPager)}  {@link ViewPager} QMUITabSegment   {@link PagerAdapter#getPageTitle(int)}  Tab </li> <li>  Tab  {@link #addTab(QMUITab)} Tab: <code> QMUITabSegment mTabSegment = new QMUITabSegment((getContext()); // config mTabSegment QMUITabBuilder tabBuilder = mTabSegment.tabBuilder() mTabSegment.addTab(tabBuilder.setText("item 1").build()); mTabSegment.addTab(tabBuilder.setText("item 2").build()); mTabSegment.setupWithViewPager(viewpager, false); //false,adapter </code> </li> <li> tab, {@link #updateTabText(int,String)}  {@link #replaceTab(int,QMUITab)}<code> mTabSegment.updateTabText(1, "update item content"); mTabSegment.replaceTab(1, tabBuilder.setText("replace item").build()); </code> </li> <li> Tab,addTab {@link #reset()}addTab {@link #notifyDataChanged()} View<code> mTabSegment.reset(); // update mTabSegment with new config QMUITabBuilder tabBuilder = mTabSegment.tabBuilder() mTabSegment.addTab(tabBuilder.setText("new item 1").build()); mTabSegment.addTab(tabBuilder.setText("new item 1").build()); mTabSegment.notifyDataChanged(); </code> </li> </ul>
 * @author cginechen
 * @date 2016-01-27
 */
public class QMUITabSegment extends HorizontalScrollView implements IQMUILayout, IQMUISkinHandlerView, IQMUISkinDefaultAttrProvider {
  private static final String TAG="QMUITabSegment";
  public static final int MODE_SCROLLABLE=0;
  public static final int MODE_FIXED=1;
  private static final int NO_POSITION=-1;
  private final ArrayList<OnTabSelectedListener> mSelectedListeners=new ArrayList<>();
  private Container mContentLayout;
  private int mCurrentSelectedIndex=NO_POSITION;
  private int mPendingSelectedIndex=NO_POSITION;
  private QMUITabIndicator mIndicator=null;
  /** 
 * TabSegmentMode
 */
  @Mode private int mMode=MODE_FIXED;
  /** 
 * item gap in MODE_SCROLLABLE
 */
  private int mItemSpaceInScrollMode;
  /** 
 * the scrollState of ViewPager
 */
  private int mViewPagerScrollState=ViewPager.SCROLL_STATE_IDLE;
  private QMUITabAdapter mTabAdapter;
  private QMUITabBuilder mTabBuilder;
  private Animator mSelectAnimator;
  private OnTabClickListener mOnTabClickListener;
  private ViewPager mViewPager;
  private PagerAdapter mPagerAdapter;
  private DataSetObserver mPagerAdapterObserver;
  private ViewPager.OnPageChangeListener mOnPageChangeListener;
  private OnTabSelectedListener mViewPagerSelectedListener;
  private AdapterChangeListener mAdapterChangeListener;
  private boolean mIsInSelectTab=false;
  private QMUILayoutHelper mLayoutHelper;
  private Map<String,Integer> mDefaultSkinAttrs=new HashMap<>();
  public QMUITabSegment(  Context context){
    this(context,null);
  }
  public QMUITabSegment(  Context context,  AttributeSet attrs){
    this(context,attrs,R.attr.QMUITabSegmentStyle);
  }
  public QMUITabSegment(  Context context,  AttributeSet attrs,  int defStyleAttr){
    super(context,attrs,defStyleAttr);
    setWillNotDraw(false);
    mLayoutHelper=new QMUILayoutHelper(context,attrs,defStyleAttr,this);
    init(context,attrs,defStyleAttr);
    setHorizontalScrollBarEnabled(false);
    setClipToPadding(false);
    setClipChildren(false);
  }
  private void init(  Context context,  AttributeSet attrs,  int defStyleAttr){
    TypedArray array=context.obtainStyledAttributes(attrs,R.styleable.QMUITabSegment,defStyleAttr,0);
    boolean hasIndicator=array.getBoolean(R.styleable.QMUITabSegment_qmui_tab_has_indicator,false);
    int indicatorHeight=array.getDimensionPixelSize(R.styleable.QMUITabSegment_qmui_tab_indicator_height,getResources().getDimensionPixelSize(R.dimen.qmui_tab_segment_indicator_height));
    boolean indicatorTop=array.getBoolean(R.styleable.QMUITabSegment_qmui_tab_indicator_top,false);
    boolean indicatorWidthFollowContent=array.getBoolean(R.styleable.QMUITabSegment_qmui_tab_indicator_with_follow_content,false);
    mIndicator=createTabIndicatorFromXmlInfo(hasIndicator,indicatorHeight,indicatorTop,indicatorWidthFollowContent);
    int normalTextSize=array.getDimensionPixelSize(R.styleable.QMUITabSegment_android_textSize,getResources().getDimensionPixelSize(R.dimen.qmui_tab_segment_text_size));
    normalTextSize=array.getDimensionPixelSize(R.styleable.QMUITabSegment_qmui_tab_normal_text_size,normalTextSize);
    int selectedTextSize=normalTextSize;
    selectedTextSize=array.getDimensionPixelSize(R.styleable.QMUITabSegment_qmui_tab_selected_text_size,selectedTextSize);
    mTabBuilder=new QMUITabBuilder(context).setTextSize(normalTextSize,selectedTextSize).setIconPosition(array.getInt(R.styleable.QMUITabSegment_qmui_tab_icon_position,QMUITab.ICON_POSITION_LEFT));
    mMode=array.getInt(R.styleable.QMUITabSegment_qmui_tab_mode,MODE_FIXED);
    mItemSpaceInScrollMode=array.getDimensionPixelSize(R.styleable.QMUITabSegment_qmui_tab_space,QMUIDisplayHelper.dp2px(context,10));
    array.recycle();
    mContentLayout=new Container(context);
    addView(mContentLayout,new FrameLayout.LayoutParams(ViewGroup.LayoutParams.WRAP_CONTENT,ViewGroup.LayoutParams.MATCH_PARENT));
    mTabAdapter=createTabAdapter(mContentLayout);
    mDefaultSkinAttrs.put(QMUISkinValueBuilder.BOTTOM_SEPARATOR,R.attr.qmui_tab_separator_color);
    mDefaultSkinAttrs.put(QMUISkinValueBuilder.TOP_SEPARATOR,R.attr.qmui_tab_separator_color);
    mDefaultSkinAttrs.put(QMUISkinValueBuilder.BACKGROUND,R.attr.qmui_tab_bg);
  }
  protected QMUITabAdapter createTabAdapter(  ViewGroup tabParentView){
    return new QMUITabAdapter(this,tabParentView);
  }
  protected QMUITabIndicator createTabIndicatorFromXmlInfo(  boolean hasIndicator,  int indicatorHeight,  boolean indicatorTop,  boolean indicatorWidthFollowContent){
    if (!hasIndicator) {
      return null;
    }
    return new QMUITabIndicator(indicatorHeight,indicatorTop,indicatorWidthFollowContent);
  }
  public QMUITabBuilder tabBuilder(){
    return new QMUITabBuilder(mTabBuilder);
  }
  /** 
 * replace with custom indicator
 * @param indicator if null, present there is not a indicator
 */
  public void setIndicator(  @Nullable QMUITabIndicator indicator){
    mIndicator=indicator;
    mContentLayout.requestLayout();
  }
  public void setItemSpaceInScrollMode(  int itemSpaceInScrollMode){
    mItemSpaceInScrollMode=itemSpaceInScrollMode;
  }
  /** 
 * clear all tabs
 */
  public void reset(){
    mTabAdapter.clear();
    mCurrentSelectedIndex=NO_POSITION;
    if (mSelectAnimator != null) {
      mSelectAnimator.cancel();
      mSelectAnimator=null;
    }
  }
  /** 
 * add a tab to QMUITabSegment
 * @param tab QMUITab
 * @return return this to chain
 */
  public QMUITabSegment addTab(  QMUITab tab){
    mTabAdapter.addItem(tab);
    return this;
  }
  /** 
 * notify dataChanged event to QMUITabSegment
 */
  public void notifyDataChanged(){
    mTabAdapter.setup();
    populateFromPagerAdapter(false);
  }
  public void addOnTabSelectedListener(  @NonNull OnTabSelectedListener listener){
    if (!mSelectedListeners.contains(listener)) {
      mSelectedListeners.add(listener);
    }
  }
  public void removeOnTabSelectedListener(  @NonNull OnTabSelectedListener listener){
    mSelectedListeners.remove(listener);
  }
  public void clearOnTabSelectedListeners(){
    mSelectedListeners.clear();
  }
  public int getMode(){
    return mMode;
  }
  public void setMode(  @Mode int mode){
    if (mMode != mode) {
      mMode=mode;
      mContentLayout.invalidate();
    }
  }
  void onClickTab(  int index){
    if (mSelectAnimator != null || mViewPagerScrollState != ViewPager.SCROLL_STATE_IDLE) {
      return;
    }
    QMUITab model=mTabAdapter.getItem(index);
    if (model != null) {
      selectTab(index,false,true);
    }
    if (mOnTabClickListener != null) {
      mOnTabClickListener.onTabClick(index);
    }
  }
  void onDoubleClick(  int index){
    if (mSelectedListeners.isEmpty()) {
      return;
    }
    QMUITab model=mTabAdapter.getItem(index);
    if (model != null) {
      dispatchTabDoubleTap(index);
